tasks.task:
docker pull pegi3s/r_deseq2:${rDeseq2Version}
docker pull pegi3s/cutadapt:${cutadaptVersion}
docker pull pegi3s/bowtie1:${bowtieVersion}
docker pull pegi3s/feature-counts:${featureCountsVersion}
docker pull pegi3s/samtools_bcftools:${samtoolsVersion}
docker pull pegi3s/qualimap:${qualimapVersion}

export path_output="${workingDir}/${outDir}/${dsqOut}" export path_output_docker="/data/${outDir}/${dsqOut}"
conversion_file="${path_output}/conditions_file.txt"
# get the reference factor for the comparison (eg.: control) ref_factor=$(tail --lines=+2 ${workingDir}/${contrast} | sed 's/".*" = //' | sed 's/".*-//' | sed 's/"$//')
# creates an empty file and adds the header of conditions_file. head -n1 ${workingDir}/${conditions} > ${conversion_file}
# converts the names of the conditions_file to a format recognized by the pipeline # (adds "trimmed_" at the beginning of the rootname and ".fastq.bam" at the end) echo "Converting condition_file format ${workingDir}/${conditions}" tail --lines=+2 ${workingDir}/${conditions} | sed 's/^/trimmed_/' | sed 's/\t/.fastq.bam\t/' >> ${conversion_file}
#Converts the "conditions" file to absolute paths echo "Adding full paths to the condition file" export dqCond="${path_output}/deseq_conditions_file.txt" export dqCond_docker="${path_output_docker}/deseq_conditions_file.txt"
cat "${conversion_file}" | sed 's+trimmed_*+'"${workingDir}/${outDir}/${bwtOut}/"'&+' | tr -s '/' > "${dqCond}"
echo "Running DESeq2 analysis" docker run --rm \ -v ${hostWorkingDir}:/data \ pegi3s/r_deseq2:${rDeseq2Version} \ Rscript /data/compi_scripts/${deSeq2Rscript} /data/${outDir}/${ftqOut}/all-counts.txt ${dqCond_docker} ${ref_factor}
